var todoList = {
    todos: [],
    deleteTodo: function(position){
        this.todos.splice(position, 1);
    },
    addTodo: function(todoText){
        this.todos.push({
            todoText: todoText,
            completed: false
        });
    },
    changeTodo: function(position, todoText) {
        this.todos[position].todoText = todoText;
        this.displayTodos();
    },
    toggleCompleted: function(position) {
        var todo = this.todos[position];
        todo.completed = true;

    },
    toggleAll: function() {
        var totalTodos = this.todos.length;
        var completedTodos = 0;

        for (i = 0; i < totalTodos; i++) {
            if (this.todos[i].completed === true)
            completedTodos++;
        }
       if (completedTodos === totalTodos){
           for (i = 0; i < totalTodos; i++) {
               this.todos[i].completed = false;               
           }
       } else {
           for (i = 0; i < totalTodos; i++) {
               this.todos[i].completed = true;               
           }
       }
        }

    };
    
    
    var handlers = {
      addTodo: function() {
        var addNewTaskText = document.getElementById('addNewTaskText');
        todoList.addTodo(addNewTaskText.value);
        addNewTaskText.value='';
        view.displayTodos();
      },
      deleteTodo: function() {
        var deleteTaskPositionInput = document.getElementById('deleteTaskPositionInput');
        todoList.deleteTodo(deleteTaskPositionInput.valueAsNumber);
        deleteTaskPositionInput.value = '';
        view.displayTodos();
      },
      changeTodo: function() {
        var changeTaskPositionInput = document.getElementById('changeTaskPositionInput');
        var changeTaskTextInput = document.getElementById('changeTaskTextInput');
        todoList.changeTodo(changeTaskPositionInput.valueAsNumber, changeTaskTextInput.value);
        changeTaskPositionInput.value = "";
        changeTaskTextInput.value = "";
        view.displayTodos();
      },
      toggleCompleted: function() {
        var toggleCompletedPositionInput = document.getElementById('toggleCompletedPositionInput');
        todoList.toggleCompleted(toggleCompletedPositionInput.valueAsNumber);
        toggleCompletedPositionInput.value = '';
        view.displayTodos();
      },
      toggleAll: function() {
      todoList.toggleAll();
      view.displayTodos();
      },
    };
    
    var view = {
      displayTodos: function() {
         var todosUl = document.querySelector('ul');
         todosUl.innerHTML = '';
        for (var i = 0; i < todoList.todos.length; i++) {
          var todoLi = document.createElement('li');
          var todo = todoList.todos[i];
          var todoTextWithCompletion = '';
          
          if (todo.completed === true) {
            todoTextWithCompletion = "(x) " +  todo.todoText;
          } else {
            todoTextWithCompletion = "( ) " +  todo.todoText;            
          }
          
          todoLi.textContent = todoTextWithCompletion;
          todosUl.appendChild(todoLi);
        }
      }
    };
